		{
			var chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
			var stringChars = new char[lenght];
			for (int i = 0; i < stringChars.Length; i++)
			{
				stringChars[i] = chars[rand.Next(chars.Length)];
