		public static string CreatePassword(int length)
		{
			const string valid = "abcdefghijklmnopqrstuvwxyz";
			StringBuilder res = new StringBuilder();
			while (0 < length--)
			{
				res.Append(valid[rand.Next(valid.Length)]);
			}
			return res.ToString();
		}
